import React, { useRef } from 'react';
import { auth } from "../firebase";

const signIn = () => {
    const emailRef = useRef(null);
    const passwordRef = useRef(null);
    const signUp = e => {
        e.preventDefault();
        auth.createUserWithEmailAndPassword(emailRef.current.value)
    }

