                <h1>Sign In</h1>
                <input ref={emailRef} type="email" />
                <input ref={passwordRef} type="password" />
                <button>Sign in</button>
                <h6>Not yet register?<span className="signin__link"></span></h6>
            </form>
        </div>
    )
}

export default signIn;
